    const onSubmit = function(event) {
      event.preventDefault()
      setBackgroundColor(`linear-gradient(180deg, ${inputValues.leftInputColor}, ${inputValues.rightInputColor})`)
    }


    // left = leftInputColor
      // right = rightInputColor
      // const gradient = {
      //   background: `linear-gradient(180deg, ${leftInputColor}, ${rightInputColor})`
      // }



  return (
    <div className="App" style={{backgroundImage}}>
      <div>
      <form onSubmit={onSubmit}>

          <div className='left-input'>
                  <input 
                    type='text'
                    value={inputValues.leftInputColor}
                    name='left'
                    onChange={handleChange}
                  />
          </div>
          <div className='right-input'>
                  <input 
                    type='text'
                    value={inputValues.rightInputColor}
                    name='right'
                    onChange={handleChange}
                  />
          </div>
          <div className='go'>
                <button
                  type='submit'
                > go
                </button>
          </div>
          </form>
      </div>


    </div>
  );
}

export default App;
