import './App.css';
import { useState } from 'react';


function App() {

    const [ leftInputColor, setLeftInputColor ] = useState('#ffff00')
    const [ rightInputColor, setRightInputColor ] = useState('#000044')
    const [ backgroundImage, setBackgroundColor ] = useState(`linear-gradient(180deg, '#ffff00', '#000044')`)

    const handleChangeLeft = function(event) {
      setLeftInputColor(event.target.value)
    }
    const handleChangeRight = function(event) {
      setRightInputColor(event.target.value)
    }



    const onSubmit = function(event) {
      event.preventDefault()
      setBackgroundColor(event.target.value = `linear-gradient(180deg, ${leftInputColor}, ${rightInputColor})`)
    }


    // left = leftInputColor
      // right = rightInputColor
      // const gradient = {
      //   background: `linear-gradient(180deg, ${leftInputColor}, ${rightInputColor})`
      // }



  return (
    <div className="App" style={{backgroundImage}}>
      <div>
      <form onSubmit={onSubmit}>

          <div className='left-input'>
                  <input 
                    type='text'
                    value={leftInputColor}
                    onChange={handleChangeLeft}
                  />
          </div>
          <div className='right-input'>
                  <input 
                    type='text'
                    value={rightInputColor}
                    onChange={handleChangeRight}
                  />
          </div>
          <div className='go'>
                <button
                  type='submit'
                > go
                </button>
          </div>
          </form>
      </div>


    </div>
  );
}

export default App;
