import './App.css';
import { useState } from 'react';


function App() {

    const [ leftInputColor, setLeftInputColor ] = useState('#ffff00')
    const [ rightInputColor, setRightInputColor ] = useState('#000044')

    const handleChangeLeft = function(event) {
      setLeftInputColor(event.target.value)
    }
    const handleChangeRight = function(event) {
      setRightInputColor(event.target.value)
    }

    const gradient = {
      background: 'linear-gradient(90deg, )'
    }


  return (
    <div className="App">
      <form>

        <div className='left-input'>
                <input 
                  type='text'
                  value={leftInputColor}
                  onChange={handleChangeLeft}
                />
        </div>
        <div className='right-input'>
                <input 
                  type='text'
                  value={rightInputColor}
                  onChange={handleChangeRight}
                />
        </div>
        <div className='go'>
              <button
                type='submit'
                // onClick={() => onClick()}
              > go
              </button>
        </div>
      </form>

    </div>
  );
}

export default App;
